package day03;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int day =12;
      switch (day) {
      case 1:
    	  System.out.println("day 01");
    	  break;
      case 2 :
    	  System.out.println("day 02");
    	  
      case 3 :
    	  System.out.println("day 03");
    	  break;
     default :
    	 System.out.println("no day");
    	  
      }
	}

}
